	,#{dept_code}
	,#{join_gbn_code}
	,#{salary}
	,#{kosa_reg_yn}
	,#{kosa_class_code}
	,#{mil_yn}
	,#{mil_type}
	,#{mil_level}
	,#{mil_startdate}
	,#{mil_enddate}
	,#{job_type}
	,#{gart_level}
